            var displayName = presence.Contact.Peer.DisplayName;
            var presenceStr = Rainbow.Util.SerializePresence(presence, DetailsLevel.Medium);
            WriteGreen($"Event ContactPresenceUpdated triggered: {displayName}{presenceStr}");
        }
    }
}

